mapper.select:
select count(*) from coach c inner join tuser t on c.tuserId = t.id
where 1 = 1

	and c.id = ${coachId}


	and t.name like '%${coachName}%'


	and t.phoneNumber like '%${phoneNumber}%'


	and c.type = ${type}

select c.id, t.name, t.phoneNumber, c.createTime from coach c inner join tuser t on c.tuserId = t.id
where 1 = 1

	and c.id = ${coach.coachId}


	and t.name like '%${coach.coachName}%'


	and t.phoneNumber like '%${coach.phoneNumber}%'


	and c.type = ${coach.type}

limit ${p.pageNum - 1}, ${p.numPerPage}
